import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';


class Calculator extends React.Component{

    state = {
        menuPosition: null,

    }
    displayCategories(e) {
        this.setState({ menuPosition: e.currentTarget })
    }
    handleClose(){

    }

    render(){
        const {compoundTypes} = this.props;
        return(
        <Card className = "CalculatorCard" variant="outlined" color = "secondary"> 
            <CardContent>

                <form noValidate autoComplete="off">
                    Initial investment:
                    <TextField id="standard-basic" label="0" />
                    Regular addition:
                    <TextField id="standard-basic" label="0" />
                    Interest rate:
                    <TextField id="standard-basic" label="0" />
                    Compound:
                    <Button aria-controls="simple-menu" aria-haspopup="true"  onClick = {() => this.handleCompound()}>
                        Annually
                    </Button>
                    {/* <MenuItem onClick={this.handleClose}>Profile</MenuItem>
                    <MenuItem onClick={this.handleClose}>My account</MenuItem>
                    <MenuItem onClick={this.handleClose}>Logout</MenuItem>

                    <IconButton aria-label="add" onClick={(e) => this.displayCategories(e)}>
                          <MoreVertIcon />
                        </IconButton>
                       */}

                      <Menu
                        id="long-menu"
                        anchorEl={this.state.menuPosition}
                        open={Boolean(this.state.menuPosition)}
                        onClose={() => this.hideCategories()}
                      >

                        {/* user can't delete predetermined categories */}
                        {compoundTypes.map((category, index) => (
                          <MenuItem
                            key={category}
                            onClick={() => {
                              if (index > 3) removeCategory(category)
                            }}
                          >
                            {category}
                            {index > 3 ? <RemoveIcon /> : null}

                          </MenuItem>
                        ))}

                        <MenuItem onKeyDown={e => e.stopPropagation()}>

                          <TextField
                            id="standard-basic"
                            label="New Category"
                            onChange={(e) => this.updateCategory(e)}
                            value={this.state.newCategory}
                          />

                          <AddIcon
                            onClick={() => {
                              console.log("Went")
                              addCategory(this.state.newCategory)
                              this.state.newCategory = ""
                              this.setState({ newCategory: this.state.newCategory })
                            }} />

                        </MenuItem>

                      </Menu>
                    {/* <Menu
                        id="Compound"

                        keepMounted
                        >
                        <MenuItem onClick={handleClose}>Profile</MenuItem>
                        <MenuItem onClick={handleClose}>My account</MenuItem>
                        <MenuItem onClick={handleClose}>Logout</MenuItem>
                    </Menu> */}
                    {/* <TextField id="filled-basic" label="Filled" variant="filled" />
                    <TextField id="outlined-basic" label="Outlined" variant="outlined" /> */}


                </form>
            </CardContent>

        </Card>
        );



    }


}


export default Calculator;
