  // ["02/01/2021", "35.23", "McDonalds", "Food"],
  // ["02/03/2021", "26.38", "Starbucks", "Food"],
  // ["02/05/2021", "136.83", "Runners", "Personal"],
  // ["02/07/2021", "13.28", "Movies", "Personal"],
