import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';


class StockList extends React.Component{

    render(){
        const {stockList, deleteStock}= this.props
        return(
            <TableContainer component={Paper}>
            <Table>
            <TableHead>
                <TableRow>
                <TableCell>Name</TableCell>
                <TableCell>Quantity</TableCell>
                <TableCell>Price</TableCell>
                <TableCell>Average Cost</TableCell>
                <TableCell>Market Value</TableCell>
                <TableCell>Book Cost</TableCell>
                <TableCell>Gain Loss</TableCell>
                <TableCell>Percentage of portfolio</TableCell>
                </TableRow>
            </TableHead>

            <TableBody>
                {stockList.map((stock) => {
                    return(
                    <TableRow>
                    <Button onClick = {() => editStock(stock, stock.name)}>{stock.name}</Button>
                    <Button onClick = {() => editStock(stock, stock.quantity)}>{stock.quantity}</Button>
                    <Button onClick = {() => editStock(stock, stock.price)}>{stock.price}</Button>
                    <Button onClick = {() => editStock(stock, stock.avgCost)}>{stock.avgCost}</Button>
                    <Button onClick = {() => editStock(stock, stock.name)}>{stock.mktValue}</Button>
                    <Button onClick = {() => editStock(stock, stock.name)}>{stock.bookCost}</Button>
                    <Button onClick = {() => editStock(stock, stock.name)}>{stock.gainLoss}</Button>                    
                    <Button onClick = {() => editStock(stock, stock.name)}>{stock.percentageOfPortfolio}</Button>
                    <Button variant="contained" onClick = {()=>deleteStock(stock)}>Delete</Button>
                    </TableRow>
                    )
                })}                
            </TableBody>
            </Table>
            </TableContainer>
        )
    }
}

export default StockList;
