import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import React from 'react';
import Button from '@material-ui/core/Button';
import { ThemeProvider } from '@material-ui/core/styles';
import { createMuiTheme } from '@material-ui/core/styles';
import { deepPurple } from '@material-ui/core/colors';
import { Link } from 'react-router-dom';
import CardMedia from '@material-ui/core/CardMedia';


import { AppBar, Tab, Tabs } from '@material-ui/core';


const theme = createMuiTheme({
    palette: {
        primary: {
            main: deepPurple[800],
        },
        secondary: {
            main: deepPurple[100],
        }
    },
    typography: {
        fontFamily: [
            'Poppins',
            'sans-serif',
        ].join(','),
    },
  });

class GeneralCard extends React.Component {

    render(){
        const {total} = this.props;
        return(
            <ThemeProvider theme={ theme }>
            <Card variant="outlined">
            <CardContent>
              <Typography variant="h4">
                Account overview
              </Typography>
              <br/>
              <Typography variant="h5" display="block"gutterBottom>
                Account name: <br/>
                <Typography gutterBottom>
                RRSP
                </Typography>
              </Typography>
              <Typography variant="h5" display="block" gutterBottom>
                Account number: <br/>
                <Typography gutterBottom>
                12345A6
                </Typography>
              </Typography>
              <Typography variant="h5" display="block" gutterBottom>
                Currency: <br/>
                <Typography gutterBottom>
                CAD$
                </Typography>
              </Typography>
              <Typography variant="h5" display="block" gutterBottom>
                Total amount invested: <br/>
                <Typography gutterBottom>
                ${total}
                </Typography>
              </Typography>
              <br/>
              <br/>
              <Typography variant="h7" display="block">
                If you'd like to learn more about saving and investing, visit our community page
                <Link to={'/community'} >
                <Button color="primary" >
                    <Typography variant="h7" >
                      here.
                    </Typography>
                </Button>
                </Link>
              </Typography>
              <CardMedia
                className = {classes.media}
                image="financialmanagement.png"
                title="Picture"
                height = "20%"
              />
            </CardContent>
          </Card>  
          </ThemeProvider>
        )
    }
}

export default GeneralCard;
