import React, { useRef, useEffect } from 'react'




const Canvas = props => {
    data = [{name:'Targaryen', amount:90200},
    {name:'Tully', amount:15000},
    {name:'Stark', amount:50500},
    {name:'Lannister', amount:1250800},
    {name:'Mormont', amount:16000}]
    const canvasRef = useRef(null)
    useEffect(() => {
        const canvas = canvasRef.current
        const context = canvas.getContext('2d')
        //Our first draw
        canvas.width = 800;
        canvas.height = 600;
        context.lineWidth = 2;
        context.strokeStyle = '#ff0000';     
        context.fillStyle = '#ffffff';
        context.globalCompositeOperation = "destination-over";

        context.fillRect(0, 0, context.canvas.width, context.canvas.height);
        context.globalCompositeOperation = "source-over";

        context.strokeRect(0, 0, canvas.width, canvas.height);//for white background
        let total = data.reduce( (ttl, house) => {
            return ttl + house.troops
        }, 0);

    }, [])

    return <canvas ref={canvasRef} {...props}/>
}




export default Canvas
