import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import React from 'react';
import Button from '@material-ui/core/Button';
import { ThemeProvider } from '@material-ui/core/styles';
import { createMuiTheme } from '@material-ui/core/styles';
import { deepPurple } from '@material-ui/core/colors';
import { Link } from 'react-router-dom';
import InvestmentImage from './financialmanagement.png';
import communityImage from './community.jpg'
import purpleStockImage from './purplestocks.jpg'

const theme = createMuiTheme({
    palette: {
        primary: {
            main: deepPurple[800],
        },
        secondary: {
            main: deepPurple[100],
        }
    },
    typography: {
        fontFamily: [
            'Poppins',
            'sans-serif',
        ].join(','),
    },
  });

//Shows some general account information
class GeneralCard extends React.Component {
  //the account name, accounr number, currency here are all hardcoded. This will need to be linked to 
  //some database in phase 2. 
  //the total amount invested is also based on hard coded entries(in index.js's state)
    render(){
        const {total} = this.props;
        return(
            <ThemeProvider theme={ theme }>
            <Card variant="outlined">
            <CardContent>
              <Typography variant="h4">
                Account overview
              </Typography>
              <br/>
              <Typography variant="h5" display="block"gutterBottom>
                Account name: <br/>
                <Typography variant="h6" gutterBottom>
                RRSP
                </Typography>
              </Typography>
              <Typography variant="h5" display="block" gutterBottom>
                Account number: <br/>
                <Typography variant="h" gutterBottom>
                12345A6
                </Typography>
              </Typography>
              <Typography variant="h5" display="block" gutterBottom>
                Currency: <br/>
                <Typography gutterBottom>
                CAD$
                </Typography>
              </Typography>
              <Typography variant="h5" display="block" gutterBottom>
                Total amount invested: <br/>
                <Typography gutterBottom>
                ${total}
                </Typography>
              </Typography>
              <br/>
              <br/>
              <Typography variant="h6" display="block">
                If you'd like to learn more about saving and investing, visit our community page
                <Link to={'/community'} >
                <Button color="primary" >
                    <Typography variant="h6" >
                      here.
                    </Typography>
                </Button>
                </Link>
              </Typography>
            </CardContent>
            <img className = "InvestmentImage" src = {purpleStockImage}/> 
          </Card> 

          </ThemeProvider>
        )
    }
}

export default GeneralCard;
